import React, { useState } from "react";
import { UploadFoto } from "./upload";

const opcoesIndicador = [
  "Alimentação",
  "Cartório",
  "Combustíveis",
  "Fretes e Carretos",
  "Hospedagem",
  "Impressões e Cópias",
  "Locação de Veículos, Máquinas e Equipamentos",
  "Materiais de Expediente e Utensílios",
  "Manutenção de Veículos",
  "Postagens e Malotes",
  "Taxas e Impostos",
  "Telefonia e Dados",
  "Tercerização de Serviços",
  "Translado",
  "Inscrição de Curso e Treinamento"
];

export function FormularioDespesas() {
  const [form, setForm] = useState({
    indicador: "",
    discriminacao: "",
    fornecedor: "",
    cidade: "",
    nf: "-",
    data: "",
    quantidade: 1,
    valor: "",
    foto: null
  });

  // Salva cada registro como um novo item no localStorage
  const salvar = () => {
    const registros = JSON.parse(localStorage.getItem("despesas") || "[]");
    registros.push({ ...form, id: Date.now() });
    localStorage.setItem("despesas", JSON.stringify(registros));
    setForm({
      indicador: "",
      discriminacao: "",
      fornecedor: "",
      cidade: "",
      nf: "-",
      data: "",
      quantidade: 1,
      valor: "",
      foto: null
    });
    alert("Despesa salva localmente!");
  };

  // Salva ao pressionar Enter em qualquer campo
  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      salvar();
    }
  };

  // Recebe o arquivo da foto
  const handleFoto = (file) => {
    setForm({ ...form, foto: file });
  };

  return (
    <form className="formulario" onKeyDown={handleKeyDown}>
      <label>Indicador:</label>
      <select
        value={form.indicador}
        onChange={e => setForm({ ...form, indicador: e.target.value })}
        required
      >
        <option value="">Selecione</option>
        {opcoesIndicador.map((op, i) => (
          <option key={i} value={op}>{op}</option>
        ))}
      </select>

      <label>Discriminação da despesa:</label>
      <input
        type="text"
        value={form.discriminacao}
        onChange={e => setForm({ ...form, discriminacao: e.target.value })}
        required
      />

      <label>Fornecedor:</label>
      <input
        type="text"
        value={form.fornecedor}
        onChange={e => setForm({ ...form, fornecedor: e.target.value })}
        required
      />

      <label>Cidade:</label>
      <input
        type="text"
        value={form.cidade}
        onChange={e => setForm({ ...form, cidade: e.target.value })}
        required
      />

      <label>NF:</label>
      <input
        type="text"
        value={form.nf}
        onChange={e => setForm({ ...form, nf: e.target.value })}
        placeholder="-"
      />

      <label>Data:</label>
      <input
        type="date"
        value={form.data}
        onChange={e => setForm({ ...form, data: e.target.value })}
        required
      />

      <label>Quantidade:</label>
      <input
        type="number"
        value={form.quantidade}
        min={1}
        onChange={e => setForm({ ...form, quantidade: Number(e.target.value) })}
        required
      />

      <label>Valor:</label>
      <input
        type="number"
        step="0.01"
        value={form.valor}
        onChange={e => setForm({ ...form, valor: e.target.value })}
        required
      />

      {/* Upload de foto integrado ao formulário */}
      <UploadFoto onFile={handleFoto} />

      <div style={{ marginTop: 16, width: '100%' }} className="botoes-horizontal">
        <button type="button" onClick={salvar}>Salvar</button>
        <button type="button" style={{ marginLeft: 8 }} disabled>Enviar</button>
      </div>
    </form>
  );
}
