import React, { useRef } from "react";

export function UploadFoto() {
  const inputRef = useRef();

  // Função para abrir a seleção de arquivos (câmera ou galeria)
  const handleBoxClick = () => {
    inputRef.current.click();
  };

  // Função para lidar com o arquivo selecionado
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Aqui você pode processar o arquivo, exibir preview, etc.
      alert("Foto recebida: " + file.name);
    }
  };

  return (
    <div style={{
      border: '2px dashed #fda085',
      borderRadius: 12,
      width: 260,
      height: 180,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: '#fff',
      cursor: 'pointer',
      margin: '32px auto',
      flexDirection: 'column',
      color: '#fda085',
      fontWeight: 600,
      fontSize: 18
    }}
      onClick={handleBoxClick}
      onDragOver={e => e.preventDefault()}
      onDrop={e => {
        e.preventDefault();
        const file = e.dataTransfer.files[0];
        if (file) {
          alert("Foto recebida: " + file.name);
        }
      }}
    >
      <span>Clique ou arraste uma foto aqui</span>
      <input
        ref={inputRef}
        type="file"
        accept="image/*"
        capture="environment"
        style={{ display: 'none' }}
        onChange={handleFileChange}
      />
    </div>
  );
}
