import React, { useRef, useState } from "react";

export function UploadFoto({ onFile }) {
  const inputRef = useRef();
  const [dragOver, setDragOver] = useState(false);

  // Função para abrir a seleção de arquivos (câmera ou galeria)
  const handleBoxClick = () => {
    inputRef.current.click();
  };

  // Função para lidar com o arquivo selecionado
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && onFile) {
      onFile(file);
    }
  };

  return (
    <div
      className={`upload-foto-box${dragOver ? ' dragover' : ''}`}
      tabIndex={0}
      onClick={handleBoxClick}
      onDragOver={e => {
        e.preventDefault();
        setDragOver(true);
      }}
      onDragLeave={e => {
        e.preventDefault();
        setDragOver(false);
      }}
      onDrop={e => {
        e.preventDefault();
        setDragOver(false);
        const file = e.dataTransfer.files[0];
        if (file && onFile) {
          onFile(file);
        }
      }}
    >
      <span>Clique ou arraste uma foto aqui</span>
      <input
        ref={inputRef}
        type="file"
        accept="image/*"
        capture="environment"
        style={{ display: 'none' }}
        onChange={handleFileChange}
      />
    </div>
  );
}
