import React, { useState } from "react";

export function SomarDados() {
    const [espacamento, setNumero1] = useState(0); 
    const [sementes_m, setNumero2] = useState(0);
    const [resultadoSoma, setResultadoSoma] = useState(0);
    const [resultadoQuadrado, setResultadoQuadrado] = useState(0);

    return (
        <div className="calculadora">
            <h1>CALCULADORA DE SEMENTE</h1>
            <label className="label" htmlFor="espacamento">ESPAÇAMENTO:</label>
            <input className="input" id="espacamento"
                type="number" 
                value={espacamento} 
                onChange={(e) => setNumero1(Number(e.target.value))} 
                placeholder="Digite o primeiro número" 
            />
            <label className="label" htmlFor="sementes">SEMENTES/M:</label>
            <input className="input" id="sementes"
                type="number" 
                value={sementes_m} 
                onChange={(e) => setNumero2(Number(e.target.value))} 
                placeholder="Digite o segundo número" 
            />
            <label className="label" htmlFor="peneira">PENEIRA:</label>
            <select className="select" id="peneira">
                <option value="0">Selecione uma peneira</option>
                <option value="60">60</option>
                <option value="65">65</option>
                <option value="70">70</option>
            </select>
            <button className="botao" id="btn-somar" onClick={() => setResultadoSoma(10000/(espacamento/100)) + sementes_m}>Somar</button>
            <button className="botao" id="btn-quadrado" onClick={() => setResultadoQuadrado(espacamento * espacamento)}>Quadrado do Primeiro Número</button>
            <h2 id="resultado-soma">Resultado: {resultadoSoma}</h2>
            <h2 id="resultado-quadrado">Quadrado do Primeiro Número: {resultadoQuadrado}</h2>
        </div>
    );
}
