import React from 'react';
import './styles/App.css'; // Certifique-se de que o caminho está correto

function App() {
  return (
    <div className="App">
      <h1>Olá, React!</h1>
        <p>Este é o meu primeiro aplicativo React.</p>
        <p>Estou aprendendo a construir interfaces de usuário.</p>
        <p>React é uma biblioteca poderosa para construir aplicações web.</p>
        <p>Vamos criar componentes reutilizáveis!</p>
        <p>Estou animado para aprender mais sobre React!</p>
        <p>React facilita a criação de interfaces dinâmicas.</p>
        <p>Componentes são a base de uma aplicação React.</p>

    </div>
  );
}

export default App;
