import React, { useState } from "react";

export function SomarDados() {
    const [numero1, setNumero1] = useState(0);
    const [numero2, setNumero2] = useState(0);
    const [resultadoSoma, setResultadoSoma] = useState(0);
    const [resultadoQuadrado, setResultadoQuadrado] = useState(0);

    return (
        <div className="calculadora">
            <h1>Calculadora de Soma</h1>
            <input className="input"
                type="number" 
                value={numero1} 
                onChange={(e) => setNumero1(Number(e.target.value))} 
                placeholder="Digite o primeiro número" 
            />
            <input className="input" 
                type="number" 
                value={numero2} 
                onChange={(e) => setNumero2(Number(e.target.value))} 
                placeholder="Digite o segundo número" 
            />
            <button className="botao" onClick={() => setResultadoSoma(numero1 + numero2)}>Somar</button>
            <button className="botao" onClick={() => setResultadoQuadrado(numero1 * numero1)}>Quadrado do Primeiro Número</button>
            <h2>Resultado: {resultadoSoma}</h2>
            <h2>Quadrado do Primeiro Número: {resultadoQuadrado}</h2>
        </div>
    );
}
